//Fluid youtube embeds
//Adapt all video in page with width/height of given div/element
jQuery.fn.fluidYoutubes=function(){var e=jQuery("iframe[src^='//www.youtube.com']"),t=this;e.each(function(){console.log(jQuery(this).height+" - "+jQuery(this).width);jQuery(this).data("aspectRatio",this.height/this.width).removeAttr("height").removeAttr("width")});jQuery(window).resize(function(){var n=t.width();console.log(n);e.each(function(){console.log(jQuery(this).height+" - "+jQuery(this).width);var e=jQuery(this);e.width(n).height(n*e.data("aspectRatio"))})}).resize();return this}