//Fluid youtube embeds
//Adapt all video in page with width/height of given div/element
jQuery.fn.fluidYoutubes=function(){var e=jQuery("iframe[src^='//www.youtube.com']"),t=this;e.each(function(){jQuery(this).data("aspectRatio",this.height/this.width).removeAttr("height").removeAttr("width")});jQuery(window).resize(function(){var n=t.width();e.each(function(){var e=jQuery(this);e.width(n).height(n*e.data("aspectRatio"))})}).resize();return this};